modify_file.code_change_description:
Add error handling for port conflicts and automatic port switching to the server setup. Replace the server configuration and listening code with the following:

const server = http.createServer(app);

server.listen(port);
server.on('error', onError);
server.on('listening', onListening);

function onError(error) {
  if (error.syscall !== 'listen') {
    throw error;
  }

  // Handle specific listen errors with friendly messages
  switch (error.code) {
    case 'EACCES':
      console.error(`Port ${port} requires elevated privileges`);
      process.exit(1);
      break;
    case 'EADDRINUSE':
      console.error(`Port ${port} is already in use`);
      // Attempt to use a different port by incrementing the current one
      server.listen(++port);
      break;
    default:
      throw error;
  }
}

function onListening() {
  const addr = server.address();
  console.log(`Server is running on port ${addr.port}`);
}

// make sure to require the http module at the top of the file: const http = require('http');
